mapper.insert:
INSERT INTO water_manage (soilmoisture, plant_id, userid)
SELECT moisture, #{plantid_log}, #{userid_log}
FROM plants_data
WHERE plant_id = #{plantid_log}

mapper.update:
update plants_data
set relay1 = #{relay1}
where mac_address = #{mac_address}

update plants_data
set relay2 = #{relay2}
where mac_address = #{mac_address}

mapper.select:
SELECT mac_address
FROM userplant  
WHERE userid_log = #{userid_log} AND plantid_log = #{plantid_log}

mapper.update:
update plants_data
set light = #{light}
where mac_address = #{mac_address}
